-- ----------------------------
-- Table structure for `adelephp_book`
-- ----------------------------
DROP TABLE IF EXISTS `adelephp_book`;
CREATE TABLE `adelephp_book` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `name` varchar(255) NOT NULL COMMENT '小说名',
  `gid` int(10) unsigned NOT NULL COMMENT '采集id',
  `author` varchar(255) NOT NULL COMMENT '作者',
  `type` varchar(255) DEFAULT NULL COMMENT '类型',
  `last_create_time` varchar(255) DEFAULT NULL COMMENT '最新更新时间',
  `count` int(10) unsigned DEFAULT NULL COMMENT '最大章节',
  `img` varchar(255) NOT NULL COMMENT '图片',
  `desc` varchar(255) NOT NULL COMMENT '描述',
  `last_create_section` varchar(255) DEFAULT NULL COMMENT '最新更新章节名',
  `hot` decimal(5,3) unsigned DEFAULT '0.000' COMMENT '是否热门',
  `classical` int(10) unsigned DEFAULT '0' COMMENT '是否经典',
  `banner` int(2) unsigned DEFAULT '0' COMMENT '是否上banner',
  `banner_img` varchar(255) DEFAULT NULL COMMENT 'banner图片',
  `is_update` int(2) unsigned DEFAULT '0' COMMENT '是否更新',
  `last_novel_id` int(10) unsigned DEFAULT NULL COMMENT '最新章节id',
  `last_section` varchar(255) DEFAULT NULL COMMENT '最新章节',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

