INSERT INTO `adelephp_book` VALUES ('1', '校花的贴身高手', '4', '鱼人二代', null, null, null, './public/img/1507445680444.jpg', '校花的贴身高手最新章节列：小说《校花的贴身高手》鱼人二代/著,校花的贴身高手全文阅读 一个大山里走出来的绝世高手，一块能预知未来的神秘玉佩…… 林逸是一名普通学生，不过，他还身负另外一个重任，那就是追校花！', null, '0.000', '0', '0', null, '0', null, null);

